import React, { useState, useEffect } from 'react'
import { useForm } from "react-hook-form";
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

export default function App(){

  const [data, SetData] = useState('')
  const [userInfo, setUserInfo] = useState({
    nombreRiesgo: "",
    pais: "",
    nombre: "",
    nombre_Completo: "",
    codigo: "",
    lista_Codigos: "",
    divisa: "",
    idioma: "",
    ciudad_Capital: "",
    codigo_llamada: "",
    region: "",
    sub_region: "",
    impacto: "",
    probabilidad: ""
  });
  const handleChange = (event) => {
    const { name, value } = event.target;
    setUserInfo({ ...userInfo, [name]: value });
  };


  const nombre = userInfo.name;
  const id = userInfo.phone
  const url = `http://localhost:5000/login/${nombre}/${id}`;

  console.log(url)


    fetch(url).then(

      res=> res.json()
    ).then(
        data=> {
          SetData(data)
          console.log("data",data)


        }
    )



  const { register, handleSubmit } = useForm();
  const Allowance  = (userInfo) => {
    console.log("formulario",userInfo);

    const url = `http://localhost:5000/membersIn`;
    console.log(url)

    fetch(url, {
      method: 'POST',
      headers:{ 'Content-Type': 'application/json',
      "Access-Control-Allow-Headers": "*",
      "Access-Control-Allow-Origin": "*",
      "Access-Control-Allow-Methods": "*" 

  },
      body: JSON.stringify({
        userInfo
      })

     } ).then(

      res=> res.json()
    ).then(
        data=> {
          SetData(data)
          console.log("data",data)


        }
    )

  } 

  const urlGetRiesgo = `http://localhost:5000/riesgoGet`;
  const options = [];
  console.log(urlGetRiesgo)


    fetch(urlGetRiesgo).then(

      res=> res.json()
    ).then(
        datas=> {
          SetData(datas)
          console.log("lista de riesgos",datas)




        }
    )


  const [tabIndex, setTabIndex] = useState(0);
  const [datas, setDatas] = useState([]);


  const onOptionChangeHandler = (event) => {
      setDatas(event.target.value);
      console.log(
          "User Selected Value - ",
          event.target.value
      );
  };

  const [isSubscribed, setIsSubscribed] = useState(false);

  const handleChanged = event => {
    if (event.target.checked) {
      console.log('✅ Checkbox is checked');
    } else {
      console.log('⛔️ Checkbox is NOT checked');
    }
    setIsSubscribed(current => !current);
  };
  return (
    <div>
       <Tabs selectedIndex={tabIndex} onSelect={(index) => setTabIndex(index)}>
      <TabList>
        <Tab>Insertar</Tab>
        <Tab>Actualizar</Tab>
        <Tab>Borrar</Tab>
        <Tab>Buscar</Tab>
      </TabList>
      <TabPanel>     
       <form  onSubmit={handleSubmit(Allowance)}>
       <div>
      <label htmlFor="nombreRiesgo">Nombre Riesgo</label>
     <input
     {...register("nombreRiesgo")}
       name="nombreRiesgo"
       type="text"
       value={userInfo.nombreRiesgo}
       onChange={handleChange}
     />
     </div>
     <div>
    <label htmlFor="pais">Pais</label>
     <input
     {...register("pais")}
       name="pais"
       type="text"
       value={userInfo.pais}
       onChange={handleChange}
     />
     </div>
     <div>
    <label htmlFor="fechaModificacion">Fecha</label>
     <input
     {...register("fechaModificacion")}
       name="fechaModificacion"
       type="date"
       value={userInfo.fechaModificacion}
       onChange={handleChange}
     />
     </div>
     <div>
    <label htmlFor="codigo">Codigo</label>
     <input
     {...register("codigo")}
       name="codigo"
       type="text"
       value={userInfo.codigo}
       onChange={handleChange}
     />
     </div>
     <div>
    <label htmlFor="divisa">Divisa</label>
     <input
     {...register("divisa")}
       name="divisa"
       type="text"
       value={userInfo.divisa}
       onChange={handleChange}
     />
     </div>
     <div>
