import React, { useState, useEffect } from 'react'
import { useForm } from "react-hook-form";
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

export default function App(){

  const [data, SetData] = useState('colombia')
  const [userInfo, setUserInfo] = useState({
    nombreRiesgo: "",
    pais: "",
    nombre: "",
    nombre_Completo: "",
    codigo: "",
    lista_Codigos: "",
    divisa: "",
    idioma: "",
    ciudad_Capital: "",
    codigo_llamada: "",
    region: "",
    sub_region: "",
    impacto: "",
    probabilidad: ""
  });
  const handleChange = (event) => {
    const { name, value } = event.target;
    setUserInfo({ ...userInfo, [name]: value });
  };

  const nombre = userInfo.name;
  const id = userInfo.phone
  const url = `http://localhost:5000/login/${nombre}/${id}`;

  console.log(url)
  useEffect(()=> {

    fetch(url).then(

      res=> res.json()
    ).then(
        data=> {
          SetData(data)
          console.log("data",data)


        }
    )
  }, [])


  const { register, handleSubmit } = useForm();
  const onSubmit = (userInfo) => console.log("formulario",userInfo.name);
  const [tabIndex, setTabIndex] = useState(0);
  return (
    <div>
       <Tabs selectedIndex={tabIndex} onSelect={(index) => setTabIndex(index)}>
      <TabList>
        <Tab>Insertar</Tab>
        <Tab>Actualizar</Tab>
        <Tab>Borrar</Tab>
        <Tab>Buscar</Tab>
      </TabList>
      <TabPanel>      <form  onSubmit={handleSubmit(onSubmit)}>
     <label htmlFor="name">Nombre completo</label>
     <input
     {...register("name")}
       name="name"
       type="name"
       value={userInfo.name}
       onChange={handleChange}
     />

     <label htmlFor="mail">Correo electrónico</label>
     <input
      {...register("mail")}
       name="mail"
       type="email"
       value={userInfo.mail}
       onChange={handleChange}
     />

     <label htmlFor="phone">Teléfono móvil</label>
     <input
       {...register("phone")}
       name="phone"
       type="tel"
       placeholder="+34"
       value={userInfo.phone}
       onChange={handleChange}
     />

     <input type="submit" />
   </form></TabPanel>
      <TabPanel></TabPanel>
    </Tabs>
       <h1>Insertar</h1>
      <h1>{data.nombre}</h1>


  </div>
  );
};
